/**
 * 
 * @file
 * @since 1.2.6.1
 */
UE.plugin.register('serverparam', function (){

    var me = this,
        serverParam = {};

    return {
        commands:{
            /**
             * ,
             * @command serverparam
             * @method execCommand
             * @param { String } cmd 
             * @example
             * ```javascript
             * editor.execCommand('serverparam');
             * editor.queryCommandValue('serverparam'); //
             * ```
             */
            /**
             * ,
             * @command serverparam
             * @method execCommand
             * @param { String } cmd 
             * @param { String } key 
             * @example
             * ```javascript
             * editor.execCommand('serverparam', 'name'); //name
             * ```
             */
            /**
             * ,
             * @command serverparam
             * @method execCommand
             * @param { String } cmd 
             * @param { String } key 
             * @param { String } value 
             * @example
             * ```javascript
             * editor.execCommand('serverparam', 'name', 'hello');
             * editor.queryCommandValue('serverparam'); // {'name': 'hello'}
             * ```
             */
            /**
             * ,
             * @command serverparam
             * @method execCommand
             * @param { String } cmd 
             * @param { Object } key 
             * @example
             * ```javascript
             * editor.execCommand('serverparam', {'name': 'hello'});
             * editor.queryCommandValue('serverparam'); // {'name': 'hello'}
             * ```
             */
            /**
             * ,
             * @command serverparam
             * @method execCommand
             * @param { String } cmd 
             * @param { Function } key 
             * @example
             * ```javascript
             * editor.execCommand('serverparam', function(editor){
             *     return {'key': 'value'};
             * });
             * editor.queryCommandValue('serverparam'); // {'key': 'value'}
             * ```
             */

            /**
             * 
             * @command serverparam
             * @method queryCommandValue
             * @param { String } cmd 
             * @example
             * ```javascript
             * editor.queryCommandValue( 'serverparam' ); // {'key': 'value'}
             * ```
             */
            'serverparam':{
                execCommand:function (cmd, key, value) {
                    if (key === undefined || key === null) { //,
                        serverParam = {};
                    } else if (utils.isString(key)) { //
                        if(value === undefined || value === null) {
                            delete serverParam[key];
                        } else {
                            serverParam[key] = value;
                        }
                    } else if (utils.isObject(key)) { //,
                        utils.extend(serverParam, key, true);
                    } else if (utils.isFunction(key)){ //,
                        utils.extend(serverParam, key(), true);
                    }
                },
                queryCommandValue: function(){
                    return serverParam || {};
                }
            }
        }
    }
});


